var items=document.querySelectorAll('[data-tags^="post"]'),filters=[];function toggleFilter(e){e.checked?filters.push(e.value):filters.splice(filters.indexOf(e.value),1),applyFilters()}function applyFilters(){if(filters.length)for(var e,t="and"===document.querySelector('[name="operator"]:checked').value,l=0;l<items.length;l++){e=t;for(var s=0;s<filters.length;s++)t?e&=items[l].dataset.tags.includes(filters[s]):e|=items[l].dataset.tags.includes(filters[s]);items[l].style.display=e?"":"none"}else for(l=0;l<items.length;l++)items[l].style.display=""}function resetFilters(){filters=[],applyFilters()}document.addEventListener("DOMContentLoaded",(function(){for(var e=document.getElementsByName("filter"),t=0;t<e.length;t++)e[t].onchange=function(){toggleFilter(this)};var l=document.getElementsByName("operator");for(t=0;t<l.length;t++)l[t].onchange=applyFilters}));